package jwk_test

import (
	"testing"

	"github.com/lestrrat-go/jwx/internal/json"

	"github.com/lestrrat-go/jwx/jwk"
	"github.com/stretchr/testify/assert"
)

func Test_X5CHeader(t *testing.T) {
	certs := []string{
		"",
		"",
		"",
	}

	t.Run("Marshal/Unmarshal", func(t *testing.T) {
		// The input contains padding. We can accept either as input, but only emit
		// strings encoded with padding
		certsNopad := make([]string, len(certs))
		for i, cert := range certs {
			for len(cert) > 0 && cert[len(cert)-1] == '=' {
				cert = cert[:len(cert)-1]
			}
			certsNopad[i] = cert
		}

		expected, err := json.Marshal(certs)
		if !assert.NoError(t, err, `json.Marshal should succeed`) {
			return
		}

		inputs := map[string][]string{
			"with padding":    certs,
			"without padding": certsNopad,
		}
		for k, input := range inputs {
			input := input
			t.Run(k, func(t *testing.T) {
				// Take the input, and create a json
				jsonbuf, err := json.Marshal(input)
				if !assert.NoError(t, err, `json.Marshal should succeed (for input)`) {
					return
				}

				var c jwk.CertificateChain
				if !assert.NoError(t, json.Unmarshal(jsonbuf, &c), `json.Unmarshal should succeed`) {
					return
				}

				if !assert.Len(t, c.Get(), 3, `should have three certs`) {
					return
				}

				buf, err := json.Marshal(c)
				if !assert.NoError(t, err, `json.Marshal should succeed`) {
					return
				}

				if !assert.Equal(t, expected, buf, `json output should match`) {
					return
				}
			})
		}
	})

	for _, key := range []jwk.Key{
		jwk.NewRSAPrivateKey(),
		jwk.NewRSAPublicKey(),
		jwk.NewECDSAPrivateKey(),
		jwk.NewECDSAPublicKey(),
		jwk.NewSymmetricKey(),
	} {
		key := key
		t.Run("Set X509CertChainKey", func(t *testing.T) {
			t.Run("string", func(t *testing.T) {
				if !assert.NoError(t, key.Set(jwk.X509CertChainKey, certs[0]), "Set for x5c should succeed") {
					return
				}

				gotcerts, ok := key.Get(jwk.X509CertChainKey)
				if !assert.True(t, ok, "Get for x5c should succeed") {
					return
				}

				if !assert.Len(t, gotcerts, 1, `should have 1 cert`) {
					return
				}
			})
			t.Run("[]string", func(t *testing.T) {
				if !assert.NoError(t, key.Set(jwk.X509CertChainKey, certs), "Set for x5c should succeed") {
					return
				}

				gotcerts, ok := key.Get(jwk.X509CertChainKey)
				if !assert.True(t, ok, "Get for x5c should succeed") {
					return
				}

				if !assert.Len(t, gotcerts, 3, `should have 3 certs`) {
					return
				}
			})
			t.Run("[]interface{} with string elements", func(t *testing.T) {
				tmp := make([]interface{}, len(certs))
				for i := 0; i < len(certs); i++ {
					tmp[i] = certs[i]
				}

				if !assert.NoError(t, key.Set(jwk.X509CertChainKey, tmp), "Set for x5c should succeed") {
					return
				}

				gotcerts, ok := key.Get(jwk.X509CertChainKey)
				if !assert.True(t, ok, "Get for x5c should succeed") {
					return
				}

				if !assert.Len(t, gotcerts, 3, `should have 3 certs`) {
					return
				}
			})
		})
	}
}
